FROM t_images ti LEFT JOIN t_model tm ON ti.model_id = tm.id LEFT JOIN t_collection tc ON ti.collection = tc.id LEFT JOIN t_category tca ON tca.id = ti.category WHERE 1 =1
AND ti.id=#{imageId}
AND tm.id=#{modelId}
AND tca.id=#{categoryId}
AND tc.id=#{collectionId}
and ti.suggestion ="PASS"

SELECT ti.id as imageId, ti.title, ti.image_link AS imageLink, tm.NAME AS modelName, tc.NAME AS collectionName, tca.NAME AS catogoryName FROM t_images ti LEFT JOIN t_model tm ON ti.model_id = tm.id LEFT JOIN t_collection tc ON ti.collection = tc.id LEFT JOIN t_category tca ON tca.id = ti.category WHERE 1 =1
AND tm.id=#{modelId}
AND tca.id=#{categoryId}
AND tc.id=#{collectionId}
limit 10

SELECT ti.id as imageId, ti.title, ti.image_link AS imageLink, tm.NAME AS modelName, tc.NAME AS collectionName, tca.NAME AS catogoryName FROM t_images ti LEFT JOIN t_model tm ON ti.model_id = tm.id LEFT JOIN t_collection tc ON ti.collection = tc.id LEFT JOIN t_category tca ON tca.id = ti.category WHERE 1 =1
and ti.id not in
#{item.imageId}
AND ti.id >= FLOOR((SELECT ((MAX(id)-MIN(id)+1)*RAND()+MIN(id)) FROM t_images)) LIMIT #{count}
